<script>
  // Function to apply the search filter and update the URL
  function applyFilter() {
    const form = document.getElementById('filter-form');
    const inputs = form.getElementsByTagName('input');
    const checkedFilters = [];

    for (let i = 0; i < inputs.length; i++) {
      if (inputs[i].type === 'checkbox' && inputs[i].checked) {
        checkedFilters.push('spec[]=' + encodeURIComponent(inputs[i].id));
      }
    }

    const currentUrl = new URL(window.location.href);
    const existingParams = new URLSearchParams(currentUrl.search);
    const newParams = new URLSearchParams();

    // Set the non-spec parameters from the existingParams
    for (const [key, value] of existingParams.entries()) {
      if (!key.startsWith('spec[')) {
        newParams.set(key, value);
      }
    }

    // Reset the page number to 1
    newParams.set('page', 1);

    const queryString = newParams.toString() + (checkedFilters.length > 0 ? '&' + checkedFilters.join('&') : '');
    const newUrl = currentUrl.origin + currentUrl.pathname + '?' + queryString;
    window.location.href = newUrl;
  }

  // Attach the applyFilter function to the checkboxes
  const checkboxes = document.querySelectorAll('#filter-form input[type="checkbox"]');
  checkboxes.forEach(checkbox => {
    checkbox.addEventListener('change', applyFilter);
  });
</script>


